import { createI18n } from 'react-router-i18n';

const locales = ['en', 'es'];

const translations = {
  en: {
    home: 'Home',
    english: 'English',
    about: 'About',
    topics: 'Topics',
    homePage: {
      title: 'Home',
      text: 'This is the homepage',
    },
    aboutPage: {
      title: 'About',
      text: 'About the project',
    },
    topicPage: {
      select: 'Please select a topic',
      components: 'Components',
    },
    rendering: 'rendering',
    components: 'components',
    props_v_state: 'props-v-state',
  },
  fr: {
    home: 'Accueil',
    french: 'Français',
    about: 'À propos',
    topics: 'Sujets',
    homePage: {
      title: 'Accueil',
      text: "Ceci est la page d'accueil",
    },
    aboutPage: {
      title: 'À propos',
      text: 'À propos du sujet',
    },
    topicPage: {
      select: 'Choisissez un sujet',
      rendering: 'Rendu avec React',
      components: 'Composants',
    },
    rendering: 'rendu',
    components: 'composants',
    props_v_state: 'accessoires-v-etat',
  },
  es: {
    home: 'Inicio',
    spanish: 'Español',
    about: 'Nosotros',
    topics: 'Temas',
    homePage: {
      title: 'Inicio',
      text: 'Esta es la página de Inicio',
    },
    aboutPage: {
      title: 'Nosotros',
      text: 'Esta es la página de Nosotros',
    },
    topicPage: {
      select: 'Por favor seleccione un Tema',
      rendering: 'Renderizando con React',
      components: 'Componentes',
    },
    rendering: 'Renderizado',
    components: 'Componentes',
    props_v_state: 'Estado y Props',
  },
};

const I18n = createI18n(
  locales,
  translations,
);

export default I18n;
