import React from 'react'
import { Link, NavLink, Redirect } from 'react-router-i18n';
import I18n from '../I18n';

const stripLocale = (pathname, locale) => {
    if (!locale) {
      return pathname;
    }

    return pathname.replace(`/${locale}`, '');
  };

const Header = ({ location: { pathname }, match: { params: { locale } }, history,}) => {

  return (
    <ul className="header" history={history}>
        <li>
        <NavLink ignoreLocale to={`/fr${stripLocale(pathname, locale)}`}>
            <I18n t="french">
            Français
            </I18n>
        </NavLink>
        </li>
        <li>
        <NavLink ignoreLocale to={`/en${stripLocale(pathname, locale)}`}>
            <I18n t="english">
            English
            </I18n>
        </NavLink>
        </li>
        <li>
        <NavLink ignoreLocale to={`/es${stripLocale(pathname, locale)}`}>
            <I18n t="spanish">Español</I18n>
        </NavLink>
        </li>
    </ul>
  )
}

export default Header
